\]###/e',      '$params[\'special\'][\'$1\']',      $params['windowtext' . $special]    );    $windowBodyStyle = (      $params['windowbodystyle' . $special] ?      ' style="' . $params['windowbodystyle' . $special] . '"' :      ''    );		$TSconf = array(		  'bodyTag' => '<body' . $windowBodyStyle . '>' . (        $params['windowtext' . $special] ?        $params['windowtext' . $special] . '<br />' :        ''      ),      'enable'  => 1,      'JSwindow'  => 1,      'wrap' => '<a href="javascript:close();"> | </a>',      'JSwindow.' => array(          'newWindow'  => 1,          'expand' => '20,' . ($params['windowtext' . $special] ? '40' : '20'),      ),        );    return $this->cObj->imageLinkWrap(      $message,      $params['windowurl' . $special],      $TSconf    );	}	/**	 * Create an internal link	 *	 * @param $value string (value to display)	 * @param $uid string (not used)	 * @param $params array (parameters)	 *	 * @return string (link)	 */		public function makeLink ($value, $uid='', $params=array()) {	  $special = $params['funcspecial'];		$folder = (      $params['folder' . $special] ?      $params['folder' . $special] :      '.'    );    $message = (      $params['message' . $special] ?      $params['message' . $special] :      $value    );		$TSconf = array(      'parameter'  => (        $value ?        (          $params['setuid' . $special] ?          $params['setuid' . $special] :          (            $params['valueisuid' . $special] ?            $params['_value'] :            $folder . '/' . $value          )        ) :        ''      ),      'target'  => $params['target' . $special],      'ATagParams' => (        $params['class' . $special] ?        'class="' . $params['class' . $special] . '" ' :        ''      ),    );      	return $this->cObj->typolink($message, $TSconf);	}		/**	 * Create a link for an external url	 *	 * @param $value string (value to display)	 * @param $uid string (not used)	 * @param $params array (parameters)	 *	 * @return string (link)	 */		public function makeUrlLink ($value, $uid='', $params=array()) {	  $special = $params['funcspecial'];    $message = (      $params['message' . $special] ?      $params['message' . $special] :      $value    );		$TSconf = array(      'parameter'  => (        $params['link' . $special] ?        $params['link' . $special] :        $params['_value']      ),      'extTarget'  => '_blank',    );      	return $this->cObj->typolink($message, $TSconf);	}	/**	 * Create a date in a given format	 *	 * @param $value string (value to display)	 * @param $uid string (not used)	 * @param $params array (parameters)	 *	 * @return string (formated date)	 */		public function makeDateFormat ($value, $uid='', $params=array()) {	  $special = $params['funcspecial'];	  $format = (      $params['format' . $special] ?      $params['format' . $special] :      ($params['eval' . $special]=='datetime' ? '%d/%m/%Y %H:%M' : '%d/%m/%Y')    );		return strftime($format, $value);	}  /***************************************************************  * Database methods                                          ***************************************************************/	/**	 * Query for deleting.	 *	 */	public function queries_delete(&$query) {    $func = trim($this->savlibraryConfig['queriers']['delete']);		$this->queriers->$func($query, $this->uid);	}	/**	 * Query for updating.	 *	 */	public function queries_update(&$query) {    $func = trim($this->savlibraryConfig['queriers']['update']);		return $this->queriers->$func($query, $this->uid);	}	/**	 * Check if it is a SELECT query	 *	 */	public function isSelectQuery($query) {    return preg_match('/^[ \r\t\n]*(?i)select[ ]*/', $query);  }  	/***************************************************************/	/* comfortable template methods                                */	/* The following code was taken from the VCD Library      */  /* @author	Elmar Hinz <elmar.hinz@vcd-berlin.de>              */ 	/***************************************************************/	public function replaceTemplate($array, $tpl="") { 		if(empty($tpl)) {			if(in_array('TYPE', array_map('strtoupper', array_keys($array)))) {				$out = $this->_doTemplateItem($array, $this->templateCode); 			} else {				$out = '<p>Unecpected call of function replaceTemplate.</p>';			} 		} else {			$out = $this->_doTemplate($array, $tpl);		}				return $out;	}	public function _doTemplate($inReps, $tpl) {		global $elmar;		if(is_array($inReps)) {			foreach($inReps as $k => $v) {				$reps[strtolower($k)] = $v;			}			$tpl = $this->_doTemplateRegions($reps[regions], $tpl);			$tpl = $this->_doTemplateMarkers($reps[markers], $tpl);			$tpl = $this->_doTemplateCutters($reps[cutters], $tpl); 		}		return $tpl;	}	public function _doTemplateRegions($containers, $tpl) {		$markers = array();		if(is_array($containers)) {			foreach($containers as $container => $items) {				$itemsTpl = $this->cObj->getSubpart($tpl, '###' . $container . '###');				$markers[$container] = $this->_doTemplateItems($items, $itemsTpl);			}		}		$MARKERS = array();		if(is_array($markers)) {			foreach($markers as $k => $v) $MARKERS['###'.$k.'###'] = $v;		}		$out = $this->cObj->substituteMarkerArrayCached($tpl, array(), $MARKERS, array());		return $out;	}	public function _doTemplateItems($items, $itemsTpl) {		if(is_array($items)) {			foreach($items as $counter => $components) {				$out .= $this->_doTemplateItem($components, $itemsTpl);			}		} else {			$out = $tpl;    }		return $out;	}	public function _doTemplateItem($components, $itemsTpl) {		if(is_array($components)) {			foreach($components as $k => $v) {				$comps[strtolower($k)] = $v;			}			$itemTpl = $this->cObj->getSubpart($itemsTpl, '###' . $comps['type'] . '###');		}		$out = $this->_doTemplate($comps, $itemTpl);		if ($components['WRAPPERS']['wrapitem']) {		  $out = $this->cObj->dataWrap($out, $components['WRAPPERS']['wrapitem']);    }		return $out;	}	public function _doTemplateMarkers($markers, $tpl) {		$MARKERS = array();		if(is_array($markers)) {			foreach($markers as $k => $v) $MARKERS['###' . $k . '###'] = $v;		}		$out = $this->cObj->substituteMarkerArrayCached($tpl, $MARKERS, array(),array());		return $out;	}	public function _doTemplateCutters($cuts, $tpl) {		$clips = array();		if(is_array($cuts)) {			foreach($cuts as $k => $v) {				$k = '###' . $k . '###';				$clips[$k] = $v ? '' : $this->cObj->getSubpart($tpl, $k);			}		}		$out = $this->cObj->substituteMarkerArrayCached($tpl, array(), $clips, array());		return $out;	}		/***************************************************************/	/* Recursive encoding and decoding                             */	/***************************************************************/	/* htmlspecialchars recursiv */	protected function hscRecurs( $in ) {		if(is_array($in)) {			foreach($in as $k => $v) {				$out[$k] = $this->hscRecurs($v);			}		} else {				$out = htmlspecialchars($in, ENT_QUOTES);		}		return $out;	}	/* addslashes recursiv */	protected function addslRecurs( $in ) {		if(is_array($in)) {			foreach($in as $k => $v) {				$out[$k] = $this->addslRecurs($v);			}		} else {			$out = addslashes($in);		}		return $out;	}	/* stripslashes recursiv */	protected function strslRecurs( $in ) {		if(is_array($in)) {			foreach($in as $k => $v) {				$out[$k] = $this->strslRecurs($v);			}		} else {			 $out = stripslashes($in);		}	return $out;	}}if (defined('TYPO3_MODE') && $TYPO3_CONF_VARS[TYPO3_MODE]['XCLASS']['ext/sav_library/class.tx_savlibrary.php']) {    include_once($TYPO3_CONF_VARS[TYPO3_MODE]['XCLASS']['ext/sav_library/class.tx_savlibrary.php']);}?>